import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';  // ✅ useNavigate instead of useHistory
import './Login.css';

function Login() {
  const [username, setUsername] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [otp, setOtp] = useState('');
  const [language, setLanguage] = useState('English');
  const navigate = useNavigate();  // ✅ useNavigate hook

  const handleSubmit = (e) => {
    e.preventDefault();
    // Validate login credentials or OTP here
    navigate('/home');  // ✅ redirect to Home page
  };

  const handleLanguageChange = (lang) => {
    setLanguage(lang);
  };

  return (
    <div className="container">
      <div className="logo">LinkClick</div>
      <p className="instruction">Enter your details to continue</p>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          type="tel"
          placeholder="Phone Number"
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="OTP"
          value={otp}
          onChange={(e) => setOtp(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>

      <div className="language-select">
        <span onClick={() => handleLanguageChange('తెలుగు')}>తెలుగు</span>
        <span onClick={() => handleLanguageChange('हिन्दी')}>हिन्दी</span>
        <span onClick={() => handleLanguageChange('English')}>English</span>
      </div>

      <p className="selected-language">Selected Language: {language}</p>
    </div>
  );
}

export default Login;
