import { useState } from 'react';

const LoginScreen = () => {
  const [username, setUsername] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [otp, setOtp] = useState('');
  const [language, setLanguage] = useState('english');

  const handleLogin = () => {
    // Handle login logic here
    console.log({ username, phoneNumber, otp, language });
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-50 p-4">
      <div className="w-full max-w-md">
        <div className="mb-8 flex justify-center">
          <div className="h-24 w-24 rounded-full bg-blue-500 flex items-center justify-center">
            <span className="text-white text-xl font-medium">LinkClick</span>
          </div>
        </div>

        <h2 className="text-center text-2xl text-gray-600 mb-8">Enter your details to continue</h2>

        <div className="flex flex-col gap-4">
          <input
            type="text"
            placeholder="Username"
            className="p-4 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />

          <input
            type="tel"
            placeholder="Phone Number"
            className="p-4 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={phoneNumber}
            onChange={(e) => setPhoneNumber(e.target.value)}
          />

          <input
            type="text"
            placeholder="OTP"
            className="p-4 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
          />

          <button
            onClick={handleLogin}
            className="p-4 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition duration-200"
          >
            Login
          </button>
        </div>

        <div className="flex justify-center gap-8 mt-8">
          <button 
            onClick={() => setLanguage('telugu')}
            className={`text-lg ${language === 'telugu' ? 'font-bold' : ''}`}
          >
            తెలుగు
          </button>
          <button 
            onClick={() => setLanguage('hindi')}
            className={`text-lg ${language === 'hindi' ? 'font-bold' : ''}`}
          >
            हिन्दी
          </button>
          <button 
            onClick={() => setLanguage('english')}
            className={`text-lg ${language === 'english' ? 'font-bold' : ''}`}
          >
            English
          </button>
        </div>
      </div>
    </div>
  );
};

export default LoginScreen;
