import { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // ⬅️ import this for navigation

const Login = () => {
  const [username, setUsername] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [otp, setOtp] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('english');

  const navigate = useNavigate(); // ⬅️ use this for navigation

  const handleLogin = () => {
    // You can add validation or backend check here
    console.log('Login attempt with:', { username, phoneNumber, otp });

    // Navigate to Home page after login
    navigate('/home');
  };

  const changeLanguage = (language) => {
    setSelectedLanguage(language);
    // You can implement i18n logic here later if needed
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-white">
      <div className="w-full max-w-md px-6">
        {/* Logo */}
        <div className="flex justify-center mb-10">
          <div className="bg-blue-500 rounded-full w-32 h-32 flex items-center justify-center">
            <span className="text-white text-2xl font-medium">LinkClick</span>
          </div>
        </div>

        {/* Title */}
        <h1 className="text-2xl text-gray-600 text-center mb-8">
          Enter your details to continue
        </h1>

        {/* Input Fields */}
        <div className="space-y-4">
          <input
            type="text"
            placeholder="Username"
            className="w-full p-4 border border-gray-300 rounded-md text-gray-500 focus:outline-none"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />

          <input
            type="tel"
            placeholder="Phone Number"
            className="w-full p-4 border border-gray-300 rounded-md text-gray-500 focus:outline-none"
            value={phoneNumber}
            onChange={(e) => setPhoneNumber(e.target.value)}
          />

          <input
            type="text"
            placeholder="OTP"
            className="w-full p-4 border border-gray-300 rounded-md text-gray-500 focus:outline-none"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
          />

          <button
            onClick={handleLogin}
            className="w-full p-4 bg-blue-500 text-white text-xl font-medium rounded-md hover:bg-blue-600 transition-colors"
          >
            Login
          </button>
        </div>

        {/* Language Selection */}
        <div className="flex justify-center space-x-8 mt-8">
          <button
            onClick={() => changeLanguage('telugu')}
            className={`text-lg ${selectedLanguage === 'telugu' ? 'font-bold' : 'text-gray-600'}`}
          >
            తెలుగు
          </button>
          <button
            onClick={() => changeLanguage('hindi')}
            className={`text-lg ${selectedLanguage === 'hindi' ? 'font-bold' : 'text-gray-600'}`}
          >
            हिन्दी
          </button>
          <button
            onClick={() => changeLanguage('english')}
            className={`text-lg ${selectedLanguage === 'english' ? 'font-bold' : 'text-gray-600'}`}
          >
            English
          </button>
        </div>
      </div>
    </div>
  );
};

export default Login;
