import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
const [username, setUsername] = useState('');
const [phoneNumber, setPhoneNumber] = useState('');
const [otp, setOtp] = useState('');
const [selectedLanguage, setSelectedLanguage] = useState('english');

const navigate = useNavigate();

const handleLogin = () => {
console.log('Login:', { username, phoneNumber, otp });
navigate('/home');
};

const changeLanguage = (language) => {
setSelectedLanguage(language);
};

return (
<div className="flex items-center justify-center min-h-screen bg-white">
<div className="w-full max-w-md px-6">
<div className="flex justify-center mb-10">
<div className="bg-blue-500 rounded-full w-32 h-32 flex items-center justify-center">
<span className="text-white text-2xl font-medium">LinkClick</span>
</div>
</div>
<h1 className="text-2xl text-gray-600 text-center mb-8">
Enter your details to continue
</h1>
<div className="space-y-4">
<input
type="text"
placeholder="Username"
className="w-full p-4 border border-gray-300 rounded-md"
value={username}
onChange={(e) => setUsername(e.target.value)}
/>
<input
type="tel"
placeholder="Phone Number"
className="w-full p-4 border border-gray-300 rounded-md"
value={phoneNumber}
onChange={(e) => setPhoneNumber(e.target.value)}
/>
<input
type="text"
placeholder="OTP"
className="w-full p-4 border border-gray-300 rounded-md"
value={otp}
onChange={(e) => setOtp(e.target.value)}
/>
<button onClick={handleLogin} className="w-full p-4 bg-blue-500 text-white text-xl font-medium rounded-md hover:bg-blue-600" >
Login
</button>
</div>
<div className="flex justify-center space-x-8 mt-8">
<button onClick={() => changeLanguage('telugu')}>
తెలుగు
</button>
<button onClick={() => changeLanguage('hindi')}>
हिन्दी
</button>
<button onClick={() => changeLanguage('english')}>
English
</button>
</div>
</div>
</div>
);
};

export default Login;
