import React from 'react';
import { useTranslation } from 'react-i18next';

const GovernmentServices = () => {
  const { t } = useTranslation();

  const services = [
    {
      id: 1,
      titleKey: 'gov.healthcare',
      image: '/assets/healthcare.png',
      link: 'https://www.nhm.gov.in/',
    },
    {
      id: 2,
      titleKey: 'gov.pension',
      image: '/assets/pension.png',
      link: 'https://www.epfindia.gov.in/',
    },
    {
      id: 3,
      titleKey: 'gov.housing',
      image: '/assets/housing.png',
      link: 'https://pmaymis.gov.in/',
    },
    {
      id: 4,
      titleKey: 'gov.id_docs',
      image: '/assets/id_docs.png',
      link: 'https://uidai.gov.in/',
    },
    {
      id: 5,
      titleKey: 'gov.transport',
      image: '/assets/transport.png',
      link: 'https://parivahan.gov.in/',
    },
    {
      id: 6,
      titleKey: 'gov.legal',
      image: '/assets/legal.png',
      link: 'https://www.nhp.gov.in/health-helpline/national-ambulance-services',
    },
  ];

  return (
    <div className="container mx-auto px-4 py-8 max-w-4xl">
      <h1 className="text-2xl md:text-3xl font-bold text-center mb-8">
        üõ°Ô∏è {t('gov.heading')}
      </h1>

      <div className="space-y-6">
        {services.map((service) => (
          <div
            key={service.id}
            className="flex flex-col md:flex-row items-center md:items-start bg-white rounded-lg shadow-sm hover:shadow-md transition-shadow p-4 border border-gray-100"
          >
            <div className="flex-shrink-0 mb-4 md:mb-0 md:mr-6">
              <img
                src={service.image}
                alt={t(service.titleKey)}
                className="w-32 h-32 object-cover rounded-md"
              />
            </div>

            <div className="flex flex-col items-center md:items-start">
              <h2 className="text-xl font-bold mb-2">{t(service.titleKey)}</h2>
              <a
                href={service.link}
                target="_blank"
                rel="noopener noreferrer"
                className="bg-black text-white px-3 py-1 rounded-md hover:bg-gray-800 transition-colors text-sm"
              >
                {t('gov.read_more')}
              </a>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default GovernmentServices;
